import React, { useReducer } from "react";

const counterReducer = (state, action) => {
  switch (action) {
    case "Plus":
      return (state = state + 1);
    case "Decline":
      return (state = state - 1);
    case "Delete_All":
      return 0;
    default:
      return state;
  }
};
const counter2Reducer = (state, action) => {
  switch (action.type) {
    case "Binding_Valua_1":
      return action.value;
    default:
      return state;
  }
};

const Counter = () => {
  const [counter, dispatch] = useReducer(counterReducer, 0);
  const [count2, dispatch2] = useReducer(counter2Reducer, 0);
  return (
    <div>
      <p>Counter : {counter}</p>
      <div>
        <button onClick={() => dispatch("Plus")}>Plus</button>
        <button onClick={() => dispatch("Decline")}>Decline</button>
        <button onClick={() => dispatch("Delete_All")}>Delete All</button>
      </div>
      <p>Counter2 : {count2}</p>
      <button
        onClick={() =>
          dispatch2({
            type: "Binding_Valua_1",
            value: 10,
          })
        }
      >
        Binding data
      </button>
    </div>
  );
};

export default Counter;
